import React, { useState, useEffect } from "react";
import Pagination from 'pagination-react-hooks';
import styled from 'styled-components'

const HomeList = (props) => {

  const [homes, setHomes] = useState([]);

  useEffect(() => {
    fetch("https://fast-everglades-97829.herokuapp.com/homes")
      .then(res => res.json())
      .then(result => setHomes(result.Homes))
  }, []);

  const show = (value) => (
    <Card key={value.Id}>
      <h2>{value.Description}</h2>
      <ul>
        <li>
          <h5>Model #</h5>
          <p>{value.HomeId}</p>
        </li>
        <li>
          <h5>Serial #</h5>
          <p>{value.HomeId}</p>
        </li>
      </ul>
    </Card>
  )

  return (
    <Main>
      <Container>
        <Pagination
          data={homes}
          Show={show}
          displayNumber="50"
          previousText="prev"
          nextText="next"
        />
      </Container>
    </Main>
  )
}

const Main = styled.main`
  padding: 1rem 0rem;
`;

const Card = styled.li`
  background: white;
  margin-bottom: 1rem;
`;

const Container = styled.section`
  max-width: 900px;
  margin: 0 auto;
  position: relative;
  h1 {
    margin: 0rem;
  }
  ul {
    display: flex;
    list-style: none;
    margin: 1rem 0rem 0rem;
    padding: 0rem;
    li {
      margin-right: 1rem;
      text-transform: uppercase;
    }
  }
`;

export default HomeList;
