import React, { useState, useEffect } from "react";
// import Pagination from 'react-hooks-paginator';
import styled from 'styled-components'

const HomeList = (props) => {

  const [homes, setHomes] = useState([]);
  const [updatedHomes, setUpdatedHomes] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [modelQuery, setModelQuery] = useState(props.model);

  const handleFilterChange = (e, filterType) => {
    switch (filterType) {
      case "searchQ":
        setSearchQuery(e.target.value)
        break;
      case "modelQ":
        setModelQuery(e)
        break;
      default: break;
    }
  }

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch("https://fast-everglades-97829.herokuapp.com/homes").then(res => res.json())
      setHomes(result.Homes);
      setUpdatedHomes(result.Homes.filter(product => product.IsModel === modelQuery));
    };
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    setModelQuery(props.model);
  }, [props.model]);


  useEffect(() => {
    let filteredHomes = homes;
    if (searchQuery.length) {
      filteredHomes = filteredHomes.filter(item => item.Description.toLowerCase().includes(searchQuery))
    }
    if (modelQuery !== "") {
      filteredHomes = filteredHomes.filter(product => product.IsModel === modelQuery)
    }
    setUpdatedHomes(filteredHomes)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [searchQuery, modelQuery])


  return (
    <Main>
      <Container>
        <input type="text" value={searchQuery} onChange={(e) => handleFilterChange(e, "searchQ")} autoFocus />
        {updatedHomes.map((item, key) => {
          return (
            <Card key={key}>
              <h2>{item.Description}</h2>

              <h5>{item.IsModel === true ? "Model #" : "Serial #"}</h5>
              <p>{item.HomeId}</p>
              :
            </Card>
          )
        })}
      </Container>
    </Main>
  )
}

const Main = styled.main`
  padding: 1rem 0rem;
`;

const Card = styled.div`
  background: white;
  margin-bottom: 1rem;
`;

const Container = styled.section`
  max-width: 900px;
  margin: 0 auto;
  position: relative;
  h1 {
    margin: 0rem;
  }
  ul {
    display: flex;
    list-style: none;
    margin: 1rem 0rem 0rem;
    padding: 0rem;
    li {
      margin-right: 1rem;
      text-transform: uppercase;
    }
  }
`;

export default HomeList;
