import React, { useState, useEffect } from "react";
import Pagination from 'react-hooks-paginator';
import styled from 'styled-components'

const HomeList = (props) => {

  const pageLimit = 10;
  const [homes, setHomes] = useState([]);
  const [offset, setOffset] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [currentData, setCurrentData] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");


  const handleFilterChange = (e, filterType) => {
    switch (filterType) {
      case "sQuery":
        setSearchQuery(e.target.value)
        break;
      default: break;
    }
  }

  useEffect(() => {
    fetch("https://fast-everglades-97829.herokuapp.com/homes")
      .then(res => res.json())
      .then(result => setHomes(result.Homes))
  }, []);


  useEffect(() => {
    setCurrentData(homes.slice(offset, offset + pageLimit));
  }, [offset, homes]);


  useEffect(() => {
    const results = currentData.filter(item =>
      item.Description.toLowerCase().includes(searchQuery)
    );
    setCurrentData(results)
  }, [currentData])



  return (
    <Main>
      <Container>

        <h6>STRING {searchQuery}</h6>
        <input type="text" value={searchQuery} onChange={e => handleFilterChange(e, "sQuery")} autoFocus />


        {currentData.map((item, key) => {
          return (
            <Card key={key}>
              <h2>{item.Description}</h2>
              <ul>
                <li>
                  <h5>Model #</h5>
                  <p>{item.HomeId}</p>
                </li>
                <li>
                  <h5>Serial #</h5>
                  <p>{item.HomeId}</p>
                </li>
              </ul>
            </Card>
          )
        })}
        <Pagination
          totalRecords={homes.length}
          pageLimit={pageLimit}
          pageNeighbours={2}
          setOffset={setOffset}
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
        />
      </Container>
    </Main>
  )
}

const Main = styled.main`
  padding: 1rem 0rem;
`;

const Card = styled.div`
  background: white;
  margin-bottom: 1rem;
`;

const Container = styled.section`
  max-width: 900px;
  margin: 0 auto;
  position: relative;
  h1 {
    margin: 0rem;
  }
  ul {
    display: flex;
    list-style: none;
    margin: 1rem 0rem 0rem;
    padding: 0rem;
    li {
      margin-right: 1rem;
      text-transform: uppercase;
    }
  }
`;

export default HomeList;
