import React, { useState } from "react";
import styled from 'styled-components'
import HomeList from './HomeList'

const App = () => {

  const [state, setState] = useState({
    model: true
  });
  const handleFilterChange = (e) => {
    window.scrollTo(0, 0);
    setState({
      model: e,
    });
  };

  return (
    <React.Fragment>
      <HeaderWrap>
        <Container>
          <Headline>Home Inventory</Headline>
          <FilterList>
            <FilterListItem onClick={(e) => handleFilterChange(true)} color={state.model === true ? "#0075c9" : ""}>Inventory</FilterListItem>
            <FilterListItem onClick={(e) => handleFilterChange(false)} color={state.model === false ? "#0075c9" : ""}>Available</FilterListItem>
          </FilterList>
        </Container>
      </HeaderWrap>
      <HomeList {...state} />
    </React.Fragment>
  )
}




const FilterListItem = styled.li`
  margin-right: 1rem;
  text-transform: uppercase;
  color: ${props => props.color};
  border-bottom: ${props => props.color ? "2px solid #0075c9" : "none"};
  padding: .8rem 2rem;
  font-weight: 700;
  letter-spacing: 1px;
  cursor: pointer; 
  @media (max-width: 700px) {
    padding: .8rem 1rem;
  }
`;

const HeaderWrap = styled.header`
  background: white;
  padding: 1rem 0rem 0rem;
  box-shadow: 2px 10px 30px #d7d7d7;
  position: sticky;
  top: 0;
  z-index: 9;
`;

const FilterList = styled.ul`
  display: flex;
  list-style: none;
  margin: 1rem 0rem 0rem;
  padding: 0rem;
`;

const Container = styled.div`
  max-width: 940px;
  padding: 0rem 20px;
  margin: 0 auto;
  position: relative;
`;

const Headline = styled.h1`
  margin: 0rem 0rem 2rem 0rem;
  @media (max-width: 700px) {
    margin: 0rem 0rem 1rem 0rem;
    font-size: 1.7rem;
  }
`;




export default App;
