  return (
    <Main>
      <Container>
        {homes.map((item, key) => {
          return (
            <Card key={key}>
              <h2>{item.Description}</h2>
              <ul>
                <li>
                  <h5>Model #</h5>
                  <p>{item.HomeId}</p>
                </li>
                <li>
                  <h5>Serial #</h5>
                  <p>{item.HomeId}</p>
                </li>
              </ul>
            </Card>
          )
        })}
        <Paginator
          totalRecords={homes.length}
          pageLimit={10}
          pageNeighbours={2}
          setOffset={setOffset}
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
        />
      </Container>
    </Main>
  )
}

const Main = styled.main`
  padding: 1rem 0rem;
`;

const Card = styled.div`
  background: white;
  margin-bottom: 1rem;
`;

const Container = styled.section`
  max-width: 900px;
  margin: 0 auto;
  position: relative;
  h1 {
    margin: 0rem;
  }
  ul {
    display: flex;
    list-style: none;
    margin: 1rem 0rem 0rem;
    padding: 0rem;
    li {
      margin-right: 1rem;
      text-transform: uppercase;
    }
  }
`;

export default HomeList;
