import React, { useState, useEffect } from "react";
// import Pagination from 'react-hooks-paginator';
import HomeCard from './HomeCard';
import styled from 'styled-components'

const HomeList = (props) => {

  const [homes, setHomes] = useState([]);
  const [updatedHomes, setUpdatedHomes] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [modelQuery, setModelQuery] = useState(props.model);
  const [message, setMessage] = useState("Loading...");

  const handleFilterChange = (e, filterType) => {
    switch (filterType) {
      case "searchQ":
        setSearchQuery(e.target.value)
        break;
      case "modelQ":
        setModelQuery(e)
        break;
      default: break;
    }
  }

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch("https://fast-everglades-97829.herokuapp.com/homes").then(res => res.json())
      setHomes(result.Homes);
      setUpdatedHomes(result.Homes.filter(product => product.IsModel === modelQuery));
      setMessage("Sorry, No Results");
    };
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    setModelQuery(props.model);
  }, [props.model]);


  useEffect(() => {
    let filteredHomes = homes;
    if (searchQuery.length) {
      filteredHomes = filteredHomes.filter(item => item.Description.toLowerCase().includes(searchQuery) || item.HomeId.includes(searchQuery))
    }
    if (modelQuery !== "") {
      filteredHomes = filteredHomes.filter(product => product.IsModel === modelQuery)
    }
    setUpdatedHomes(filteredHomes)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [searchQuery, modelQuery])


  return (
    <Main>
      <HomeListContainer>
        <SearchBar type="text" value={searchQuery} onChange={(e) => handleFilterChange(e, "searchQ")} placeholder="Search for a home" autoFocus />
        {updatedHomes.length === 0 ?
          (<div>{message}</div>)
          : updatedHomes.map((item, key) => {
            return (
              <HomeCard
                key={key}
                title={item.Description}
                model={item.IsModel}
                id={item.HomeId}
                image={item.ThumbnailImage}
              />
            )
          })}
      </HomeListContainer>
    </Main>
  )
}

const Main = styled.main`
  padding: 1rem 0rem;
`;

const SearchBar = styled.input`
  padding: 10px;
  margin-bottom: 1rem;
  margin-top: 2rem;
  background: transparent;
  border-bottom: 1px solid gray;
  color: gray;
  box-shadow: none;
`;

const HomeListContainer = styled.section`
  max-width: 900px;
  margin: 0 auto 40px;
  position: relative;
  h1 {
    margin: 0rem;
  }
  ul {
    display: flex;
    list-style: none;
    margin: 1rem 0rem 0rem;
    padding: 0rem;
    li {
      margin-right: 1rem;
      text-transform: uppercase;
    }
  }
`;

export default HomeList;
