import React, { useState, useEffect } from "react";
import { usePagination } from "react-use-pagination";
import styled from 'styled-components'

const HomeList = (props) => {

  const [homes, setHomes] = useState([]);

  const {
    currentPage,
    totalPages,
    setNextPage,
    setPreviousPage,
    nextEnabled,
    previousEnabled,
    startIndex,
    endIndex
  } = usePagination({ totalItems: data.length, initialPageSize: 1 });

  useEffect(() => {
    fetch("https://fast-everglades-97829.herokuapp.com/homes")
      .then(res => res.json())
      .then(result => setHomes(result.Homes))
  }, [])

  return (
    <Main>
      <Container>
        {homes.slice(startIndex, endIndex).map((item, key) => {
          return (
            <Card key={key}>
              <h2>{item.Description}</h2>
              <ul>
                <li>
                  <h5>Model #</h5>
                  <p>{item.HomeId}</p>
                </li>
                <li>
                  <h5>Serial #</h5>
                  <p>{item.HomeId}</p>
                </li>
              </ul>
            </Card>
          )
        })}
        <button onClick={setPreviousPage} disabled={!previousEnabled}>Previous Page</button>
        <span>Current Page: {currentPage} of {totalPages}</span>
        <button onClick={setNextPage} disabled={!nextEnabled}>Next Page</button>
      </Container>
    </Main>
  )
}

const Main = styled.main`
  padding: 1rem 0rem;
`;

const Card = styled.div`
  background: white;
  margin-bottom: 1rem;
`;

const Container = styled.section`
  max-width: 900px;
  margin: 0 auto;
  position: relative;
  h1 {
    margin: 0rem;
  }
  ul {
    display: flex;
    list-style: none;
    margin: 1rem 0rem 0rem;
    padding: 0rem;
    li {
      margin-right: 1rem;
      text-transform: uppercase;
    }
  }
`;

export default HomeList;
