import React, { useState } from "react";
import styled from 'styled-components'
import HomeList from './HomeList'

const App = () => {
  const [state, setState] = useState({
    model: true
  });
  const handleFilterChange = (e) => {
    setState({
      model: e,
    });
  };
  return (
    <React.Fragment>
      <HeaderWrap>
        <Container>
          <h1>Home Inventory</h1>
          <FilterList>
            <FilterListItem onClick={(e) => handleFilterChange(true)} style={{ color: state.model === true ? "#0077c0" : "" }}>Inventory</FilterListItem>
            <FilterListItem onClick={(e) => handleFilterChange(false)} style={{ color: state.model === false ? "#0077c0" : "" }}>Available</FilterListItem>
          </FilterList>
        </Container>
      </HeaderWrap>
      <HomeList {...state} />
    </React.Fragment>
  )
}

const HeaderWrap = styled.header`
  background: white;
  padding: 1rem 0rem;
  box-shadow: 2px 10px 30px #d7d7d7;
  position: sticky;
  top: 0;
  z-index: 9;
`;

const FilterList = styled.ul`
  display: flex;
  list-style: none;
  margin: 1rem 0rem 0rem;
  padding: 0rem;
`;

const FilterListItem = styled.li`
  margin-right: 1rem;
  text-transform: uppercase;
`;

const Container = styled.div`
  max-width: 900px;
  margin: 0 auto;
  position: relative;
  h1 {
    margin: 0rem 0rem 2rem 0rem;
  }
`;


export default App;
