import React, { useState } from "react";
import styled from 'styled-components'
import HomeList from './HomeList'

const App = () => {
  const [state, setState] = useState(true);
  const handleFilterChange = (e) => {
    setState(e);
  };
  return (
    <React.Fragment>
      <HeaderWrap>
        <Container>
          <h1>Home Inventory</h1>
          <ul>
            <li onClick={handleFilterChange(true)} style={{ color: this.state.modelCheck === true ? "#0077c0" : "" }}>Inventory</li>
            <li onClick={handleFilterChange(false)} style={{ color: this.state.modelCheck === false ? "#0077c0" : "" }}>Available</li>
          </ul>
        </Container>
      </HeaderWrap>
      <HomeList {...state} />
    </React.Fragment>
  )
}

const HeaderWrap = styled.header`
  background: white;
  padding: 1rem 0rem;
  box-shadow: 2px 10px 30px #d7d7d7;
  position: sticky;
  top: 0;
  z-index: 9;
`;

const Container = styled.div`
  max-width: 900px;
  margin: 0 auto;
  position: relative;
  h1 {
    margin: 0rem 0rem 2rem 0rem;
  }
  ul {
    display: flex;
    list-style: none;
    margin: 1rem 0rem 0rem;
    padding: 0rem;
    li {
      margin-right: 1rem;
      text-transform: uppercase;
    }
  }
`;


export default App;
